#include <stdio.h>
#include <stdlib.h>
#include <string.h>

int main() {
    char sequence[] = "";
    
    int length = strlen(sequence);
    long long int max_product = 0;
    char max_digits[6] = "";
    
    for (int i = 0; i < length - 4; i++) {
        long long int product = 1;
        for (int j = i; j < i + 5; j++) {
            product *= sequence[j] - '0';
        }
        if (product > max_product) {
            max_product = product;
            strncpy(max_digits, sequence + i, 5);
        }
    }
    
    printf("5 digitos: %s\n", max_digits);
    printf("Valor do produto: %lld\n", max_product);
    
    return 0;
}
